from pandas import DataFrame
from data_transformers import chain, transformer


#  DEFINITIONS_START
@transformer.convert
def join_dataset(df: pd.DataFrame, df2: str, how : str = 'left', on: str = None, left_on: str = None, right_on: str = None) -> pl.DataFrame:

    import polars as pl

    # Create safe eval context
    eval_context = {
        'pl': pl,
        'null': None,
        'None': None,
        '__builtins__': {}  # Restrict builtins for security
    }

    df2 = eval(df2, eval_context)

    if on is not None:
        return df.join(df2, how=how, on=on)
    else:
        return df.join(df2, how=how, left_on=left_on, right_on=right_on)

@transformer.convert
def multiplicar_por_escalar(df: pl.DataFrame, col: str, k: float):
    return df.with_columns(
        (pl.col(col) * k).alias(col)
    )

@transformer.convert
def rename_cols(df: pl.DataFrame, map):
    return df.rename(map)

@transformer.convert
def drop_col(df: pl.DataFrame, col):
        return df.drop(col)
#  DEFINITIONS_END


